from aiogram import Bot, Dispatcher, executor, typesfrom aiogram.dispatcher.filters import Textfrom token_bot import tokenfrom async_main import collect_datafrom aiofiles import osbot = Bot(token)dp = Dispatcher(bot)@dp.message_handler(commands='start')async def start(message: types.Message):    start_buttons = ['Saint-Petersburg', 'Moscow']    keyboard = types.ReplyKeyboardMarkup(resize_keyboard=True)    keyboard.add(*start_buttons)    await message.answer('Please select a City', reply_markup=keyboard)@dp.message_handler(Text(equals='Saint-Petersburg'))async def spb_city(message: types.Message):    await message.answer('Please wait...')    chat_id = message.chat.id    await send_data(store_id='61006', chat_id=chat_id)@dp.message_handler(Text(equals='Moscow'))async def moscow_city(message: types.Message):    await message.answer('Please wait...')    chat_id = message.chat.id    await send_data(store_id='161433', chat_id=chat_id)async def send_data(store_id='', chat_id=''):    file = await collect_data(store_id=store_id)    await bot.send_document(chat_id=chat_id, document=open(file, 'rb'))    await os.remove(file)if __name__ == '__main__':    executor.start_polling(dp)