import asyncioimport datetimefrom aiofiles import osfrom aiogram import Bot, Dispatcher, types, Ffrom aiogram.filters import CommandStartfrom aiogram.enums import ParseModefrom aiogram.methods import SendDocumentfrom aiogram.types import Messagefrom magnit_token import tokenfrom async_main import collect_datafrom keyboards.reply import citybot = Bot(token, parse_mode=ParseMode.HTML)dp = Dispatcher()@dp.message(CommandStart())async def start(message: types.Message):    await message.answer('Please select a City', reply_markup=city)@dp.message(F.text.lower() == 'saint-petersburg')async def spb_city(message: types.Message):    await message.answer('Please wait...')    chat_id = str(message.chat.id)    await send_data(store_id='61006', chat_id=chat_id)@dp.message(F.text.lower() == 'moscow')async def moscow_city(message: types.Message):    await message.answer('Please wait...')    chat_id = str(message.chat.id)    await send_data(store_id='161433', chat_id=chat_id)async def send_data(store_id='', chat_id=''):    file = await collect_data(store_id=store_id)    await bot(SendDocument(chat_id=chat_id, document=open(file, 'rb')))    await os.remove(file)async def main():    await dp.start_polling(bot)if __name__ == '__main__':    asyncio.run(main())